pragma solidity ^0.4.17;

contract MessageStore {

    address[] public recipients;
    string[] public postBox;

    //allows storage of messages
    function storeMsg(string mail, uint date, address recipeient) public returns(bool) {
        //possibly adjust in the future to 
        recipients[date] = recipeient;
        postBox[date] = mail;
    }

    function getRecipients() public view returns (address[]) {
        return recipients;
    }

    function getRecipient(uint date) public view returns (address) {
        return recipients[date];
    }

    function getMsg(uint date) public view returns (string) {
        require(date < block.timestamp);
        return postBox[date];
    }
}
